mapper.select:
select /* CommCommentDao.getComments */ m.comment_idx as comment_idx , m.comment_cd as comment_cd , c1.cd_nm as comment_cd_nm , m.post_idx as post_idx , m.member_idx as member_idx , if(m.use_yn = 'N', '삭제된 댓글입니다', m.title ) as title , if(m.use_yn = 'N', '삭제된 댓글입니다', m.content ) as content , m.parent_comment_idx as parent_comment_idx , m.use_yn as use_yn , m.reg_date as reg_date , d1.view_count as view_count , d1.like_count as like_count , d1.unlike_count as unlike_count , (select count(*) from comment i_d where i_d.parent_comment_idx = m.comment_idx) as children_cnt , if(isnull(d2.comment_idx), 'N', 'Y') as like_yn , if(isnull(d3.comment_idx), 'N', 'Y') as unlike_yn from comment m inner join comment_info d1 on m.comment_idx = d1.comment_idx left join comment_info_like d2 on m.comment_idx = d2.comment_idx and d2.member_idx = #{memberIdx} left join comment_info_unlike d3 on m.comment_idx = d3.comment_idx and d3.member_idx = #{memberIdx} left join cm_cd c1 on c1.grp_cd = 'CMMT' and m.comment_cd = c1.cd where 1=1 and m.parent_comment_idx = #{parentCommentIdx}
and m.comment_cd = #{commentCd}
and m.post_idx = #{postIdx}
and m.member_idx = #{memberIdx}
and m.title = #{title}
and m.content = #{content}
order by m.comment_idx desc limit #{limit} offset #{offset}

select /* CommCommentDao.getCommentsTotCnt */
       count(*) as tot_cnt
  from            comment       m
       inner join comment_info        d1 on m.comment_idx = d1.comment_idx
       left  join comment_info_like   d2 on m.comment_idx = d2.comment_idx and d2.member_idx = #{memberIdx}
       left  join comment_info_unlike d3 on m.comment_idx = d3.comment_idx and d3.member_idx = #{memberIdx}
       left  join cm_cd               c1 on c1.grp_cd     = 'CMMT'         and m.comment_cd  = c1.cd
 where 1=1
   and m.parent_comment_idx = #{parentCommentIdx}

   and m.comment_cd = #{commentCd}


   and m.post_idx = #{postIdx}


   and m.member_idx = #{memberIdx}


   and m.title = #{title}


   and m.content = #{content}

mapper.insert:
insert /* CommCommentDao.createComment */
  into comment (
       comment_cd
     , post_idx
     , member_idx
     , title
     , content
     , parent_comment_idx
     , use_yn
     , reg_date
) values (
       #{commentCd}
     , #{postIdx}
     , #{memberIdx}
     , #{title}
     , #{content}
     , #{parentCommentIdx}
     , #{useYn}
     , date_format(current_timestamp(),'%Y%m%d%H%i%s')
)

mapper.update:
update /* CommCommentDao.updateComment */
       comment
   set title   = #{title}
     , content = #{content}
 where comment_idx = #{commentIdx}

mapper.select:
select /* CommCommentDao.getCommentLike */
       comment_idx as comment_idx
     , member_idx  as member_idx
  from comment_info_like
 where comment_idx = #{commentIdx}
   and member_idx  = #{memberIdx}

select /* CommCommentDao.getCommentUnlike */
       comment_idx as comment_idx
     , member_idx  as member_idx
  from comment_info_unlike
 where comment_idx = #{commentIdx}
   and member_idx  = #{memberIdx}

mapper.insert:
insert /* CommCommentDao.createCommentLike */
  into comment_info_like (
       comment_idx
     , member_idx
) values (
       #{commentIdx}
     , #{memberIdx}
)

insert /* CommCommentDao.createCommentUnlike */
  into comment_info_unlike (
       comment_idx
     , member_idx
) values (
       #{commentIdx}
     , #{memberIdx}
)

mapper.delete:
delete /* CommCommentDao.deleteCommentLike */
  from comment_info_like
 where comment_idx = #{commentIdx}
   and member_idx  = #{memberIdx}

delete /* CommCommentDao.deleteCommentUnlike */
  from comment_info_unlike
 where comment_idx = #{commentIdx}
   and member_idx  = #{memberIdx}
