mapper.insert:
insert /* NoticeCommentDao.createNoticeComment */
  into notice_comment (
       notice_idx
     , member_idx
     , title
     , content
     , use_yn
) values (
       #{noticeIdx}
     , #{memberIdx}
     , #{title}
     , #{content}
     , #{useYn}
)

mapper.update:
update /* NoticeCommentDao.updateNoticeComment */
       notice_comment
   set title   = #{title}
     , content = #{content}
     , use_yn  = #{useYn}
 where notice_comment_idx = #{noticeCommentIdx}

mapper.delete:
delete /* NoticeCommentDao.deleteNoticeComment */
  from notice_comment
 where notice_comment_idx = #{noticeCommentIdx}

delete /* NoticeCommentDao.deleteNoticeCommentInfo */
  from notice_comment_info
 where notice_comment_idx = #{noticeCommentIdx}
