mapper.select:
select /* NoticeCommentHistoryDao.getLikeHistory */
       notice_comment_idx as notice_idx
     , member_idx         as member_idx
  from notice_comment_info_like_history
 where notice_comment_idx = #{noticeCommentIdx}
   and member_idx         = #{memberIdx}

mapper.insert:
insert /* NoticeCommentHistoryDao.createLikeHistory */
  into notice_comment_info_like_history (
       notice_comment_idx
     , member_idx
) values (
       #{noticeCommentIdx}
     , #{memberIdx}
)

mapper.delete:
delete /* NoticeCommentHistoryDao.deleteLikeHistory */
  from notice_comment_info_like_history
 where notice_comment_idx = #{noticeCommentIdx}
   and member_idx         = #{memberIdx}

mapper.select:
select /* NoticeCommentHistoryDao.getUnlikeHistory */
       notice_comment_idx as notice_idx
     , member_idx         as member_idx
  from notice_comment_info_unlike_history
 where notice_comment_idx = #{noticeCommentIdx}
   and member_idx         = #{memberIdx}

mapper.insert:
insert /* NoticeCommentHistoryDao.createUnlikeHistory */
  into notice_comment_info_unlike_history (
       notice_comment_idx
     , member_idx
) values (
       #{noticeCommentIdx}
     , #{memberIdx}
)

mapper.delete:
delete /* NoticeCommentHistoryDao.deleteUnlikeHistory */
  from notice_comment_info_unlike_history
 where notice_comment_idx = #{noticeCommentIdx}
   and member_idx         = #{memberIdx}
