mapper.select:
select /* TeamDao.getTeams */ team_idx as team_idx , team_nm as team_nm , team_desc as team_desc , date_format(create_date, '%Y-%m-%d %H:%i:%s') as create_date , join_code as join_code from team where 1=1
and team_idx = #{teamIdx}
and team_nm = #{teamNm}

mapper.insert:
insert /* TeamDao.createTeam */
  into team (
       team_nm
     , team_desc
     , join_code
) values (
       #{teamNm}
     , #{teamDesc}
     , #{joinCode}
)

mapper.update:
update /* TeamDao.updateTeam */
       team
   set team_nm   = #{teamNm}
     , team_desc = #{teamDesc}
     , join_code = #{joinCode}
 where team_idx = #{teamIdx}

mapper.delete:
delete /* TeamDao.deleteTeam */
  from team
 where team_idx = #{teamIdx}
